import React from 'react';
import {connect} from "react-redux";
import MaximizeContent from "./MaximizeContent";
import {Row, Col, Card, CardBody, Button, ButtonGroup} from 'reactstrap';
import {modalImportToggle} from "../redux/actions";
import SelectRegion from './SelectRegion';
import SelectRadioSite from './SelectRadioSite';
import ReactTooltip from 'react-tooltip'

// REST
import RestApiModule from '../RestApiModule'
// Alert
import AlertModule from '../AlertModule'

class ToolbarMatrix extends React.Component {

    constructor(props) {
        super(props)

        this.restApi = new RestApiModule();
        this.alert = new AlertModule();
    }

    componentDidUpdate(prevProps, prevState, snapshot) {
        ReactTooltip.rebuild();
    }

    openImportModal() {

        const {token, setModalImport, language} = this.props

        this.restApi.callApi('getImportStatus', {
            token
        }).then(response => {

            const {isImportOngoing, user} = response;

            if (!isImportOngoing) {
                setModalImport()
            } else {
                const message = language.importSection.ongoingImportText.replace('{username}', user);

                this.alert.showMessage(
                    'warning',
                    language.importSection.ongoingImportTitle,
                    message,
                    false)
            }
        })
    }

    render() {

        const {language, wrapper, tooltipPlacement} = this.props;

        if (language) {

            return (
                <Card className={wrapper}>
                    <CardBody>

                        <Row>
                            <Col xs={2}>
                                <SelectRegion
                                    handleRegionChange={this.props.handleRegionChange.bind(this)}
                                />
                            </Col>

                            <Col xs={2}>
                                <SelectRadioSite
                                    handleRadioSiteChange={this.props.handleRadioSiteChange.bind(this)}
                                />
                            </Col>
                            <Col xs={8} className="text-right tools">

                                <ButtonGroup>
                                {
                                language.matrixViewToolbar.map((item, key) => {

                                    if ((item.clickEvent === 'edit' ||
                                        item.id === 'saveOptions')) {
                                        item.showMe = !this.props.editOn
                                    }

                                    // disable buttons if edit mode on
                                    let disableAttr = this.props.editOn ? item.showMe : false;

                                    // if data empty just import button can be clickable
                                    if (!this.props.isDataExist) {
                                        disableAttr = !(item.clickEvent === 'import');

                                    }

                                    if (item.id !== 'saveOptions') {
                                        return (
                                            <Button key={key}
                                                    data-tip={item.title}
                                                    data-place={tooltipPlacement}
                                                    disabled={disableAttr}
                                                    className={item.showMe
                                                        ? undefined
                                                        : 'd-none'
                                                    }
                                                    onClick={item.clickEvent === 'import'
                                                        ? this.openImportModal.bind(this)
                                                        : this.props[item.clickEvent]
                                                    }
                                                    color={item.btnClass}>
                                                <i className={`icon ${item.icon}`}/>
                                            </Button>
                                        )
                                    } else if (item.id === 'saveOptions' && this.props.editOn) {

                                        return (

                                            <div className="dropdown" key={key}>
                                                <Button data-tip={item.title}
                                                        data-place={tooltipPlacement}
                                                        color={item.btnClass}
                                                        data-toggle="dropdown">
                                                    <i className={`icon ${item.icon}`}/>
                                                </Button>
                                                <div className="dropdown-menu dropdown-menu-right">
                                                    {item.children.map((child, childKey) => {

                                                        const showOption = this.props.newRowAdded === true
                                                            ? child.showOnNewRow
                                                            : true;

                                                        const divClass = `${child.class} ${
                                                            (showOption) ? '' : ' d-none'
                                                            }`;

                                                        return (
                                                            <div className={divClass}
                                                               onClick={this.props[child.clickEvent]}
                                                               key={childKey}>
                                                                <i className={`icon ${child.icon} mr-2`}/>
                                                                {child.title}
                                                            </div>
                                                        )
                                                    })
                                                    }
                                                </div>
                                            </div>
                                        )
                                    }
                                })
                                }
                                </ButtonGroup>

                                <MaximizeContent/>
                            </Col>
                        </Row>
                    </CardBody>
                </Card>

            )
        } else {
            return (
                <></>
            )
        }

    }
}

function mapStateToProps(state) {
    return {
        token: state.token,
        language: state.language,
        editOn: state.editOn,
        maximize: state.maximize,
        tooltipPlacement: state.tooltipPlacement
    }
}
function mapDispatchToProps(dispatch) {
    return {
        setModalImport: () => dispatch(modalImportToggle())
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(ToolbarMatrix);
