                if (json.hasOwnProperty(key)) {
                    this.state.newArrColumns.push(key);

                }
            }

            for(let a=0; a<jsonBig.length; a++){
                const jsonPair = response.data[a];
                for(let key in jsonPair){
                    if (jsonPair.hasOwnProperty(key)) {
                        this.state.newArrData.push(jsonPair[key]);
                    }
                }
                this.state.data.push(this.state.newArrData);
                this.state.newArrData = [];
            }


            /*const options = {
                color : "color10",
                label : "x"
            };
            for(let k = 0; k<10; k++){

                this.state.columnOptions.push(options);

            }*/


            console.log(this.state.columnOptions);
            this.state.columns.push(this.state.newArrColumns);
            this.forceUpdate();

            this.hot = this.hotTableComponent.current.hotInstance;

            console.log(this.hot)
            //this.hot.setCellMeta(1, 4, "className", 'bg-primary text-white');
            //this.hot.render();
           /* var rows=this.hot.countRows();  // get the count of the rows in the table
            var cols=this.hot.countCols();  // get the count of the columns in the table.
            for(var row=0; row<rows; row++){  // go through each row of the table

                    var cell = this.hot.getCell(row,1);
                    cell.style.background = "#00FF90";

            }

            console.log(this.hot.getSettings())*/

          //  this.hot.render();





            // Create a copy of map file
            const headerMap = JSON.parse(JSON.stringify(groupingMap));

            // Set the header groups for table
            const headerGrouping = headerMap.filter((item) => {
                item.colspan = item.columns.length;
                return typeof item.columns === "object"
            })

            // set each and every column defaults
            const cols = headerMap.map((item) => {

                return item.columns.map(c => {

                    this.titles.push(c.label);

                    c.color = item.color;

                    c.editor = false; // default readonly

                    if (typeof c.validator !== "undefined") {

                        if (c.validator === 'ipValidator') {
                            c.validator = (value, callback) => {
                                this.ipValidate(value, callback)
                            }
                        }

                        if (c.validator === 'isNumeric') {
                            c.validator = (value, callback) => {
                                this.numericValidate(value, callback)
                            }
                        }
                    }

                    if (typeof c.type !== "undefined") {
                        if (c.type === 'dropdown') {
                            this.setPickList(c.data).then((list) => {
                                c.source = list;
                            });
                        }
                    }

                    if (typeof c.rerenderer !== "undefined") {
                        c.renderer = this.iconForLockStatusInCell.bind(this);
                        delete c.rerenderer;
                    }

                    return c;
                });
            })
            console.log(this.columns);

            // spread the columns into single object
            cols.map((c) => {
                console.log(c);
                this.columns = [...this.columns, ...c];
            })



        }).catch((err) => {
    console.log(err);
})

