    render() {
        return(
            <>
                <Header/>
                <Row className="h-100">
                    <Sidebar maximize={this.props.maximize}/>
                    <Col className={this.props.contentCSS}>

                        <Card className={'mt-1 fixedCard'}>
                            <CardBody>
                                <HotTable  ref={this.hotTableComponent} id={'hot'} data={this.state.data}  autoRowSize={true}  autoWrapRow={true} dropdownMenu={true} filters={true} manualColumnResize={true} columnSorting={true} colHeaders={this.state.columns[0]} columns={this.state.columnOptions} rowHeaders={true} width="1900" height="600" licenseKey="non-commercial-and-evaluation"

                                />

                            </CardBody>


                        </Card>
                    </Col>
                </Row>

            </>
        )
    }
}

function mapStateToProps(state) {
    return {
        token: state.token,
        language: state.language,
        maximize: state.maximize,
        contentCSS: state.contentCSS,
        fontSize: state.topologySelectedNodeFontSize,
        spinnerToggle: state.spinnerToggle,
    }
}
function mapDispatchToProps(dispatch) {
    return {

        setSpinner: () => dispatch(spinnerToggle()),
    }

}

export default connect(mapStateToProps,mapDispatchToProps)(WDM);
