import React from 'react';
import {connect} from "react-redux";
import {withRouter} from 'react-router-dom';
import {Row, Col, Card, CardBody, CardFooter, FormGroup, Input, Button, Alert} from 'reactstrap';

// User Privileges for accessible pages
import RoleModule from '../RoleModule';

// RestApi Connection
import RestApiModule from '../RestApiModule';

//Crypto Module
import CryptoModule from '../CryptoModule';

import LanguageButton from '../components/LanguageButton';
import Copyright from "../components/Copyright";
import Spinner from '../components/Spinner';

import {setAuthUser, setToken, spinnerToggle} from "../redux/actions";

class Login extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            username: "",
            password: "",
            loginError: false
        }

        this.roleModule = new RoleModule();
        this.restApi = new RestApiModule();
        this.crypto = new CryptoModule();
    }

    //sysadm
    //sysadm4inwi
    handleChange(event) {
        this.setState({[event.target.name]: event.target.value});
    }

    validateForm() {
        return this.state.username.length > 0 && this.state.password.length > 0;
    }

    handleSubmit() {
        this.setState({loginError: !this.validateForm()})

        if (this.validateForm()) {
            this.props.setSpinner();
            /*const u = this.crypto.encrypt(this.state.username);
            const p = this.crypto.encrypt(this.state.password);
            console.log(u);*/

            this.restApi.callApi('login', {
                username: this.crypto.encrypt(this.state.username),
                password: this.crypto.encrypt(this.state.password)
            }).then((response) => {

                // get user roles
                // TODO: get user roles and redirect to user correct page.

                this.restApi.callApi('getRoles', {
                    token: response.token
                }).then((res) => {
                    console.log(res);

                    const role = this.roleModule.getRole("Admin");
                    const user = {
                        name: this.state.username,
                        role
                    }
                    this.props.setUser(user);
                    this.props.setToken(response.token);
                    this.props.setSpinner();
                    this.props.history.push('/matrix');
                }).catch(e => console.log(e));



            }).catch((err) => {

                console.log(err);
                this.setState( {loginError: true})
                this.props.setSpinner();
            })

            /*const role = this.roleModule.getRole("Admin");

            const user = {
                name: "Demo User",
                role
            };

            this.props.setUser(user);
            this.props.setToken('RrhzQx5fyZq0JzuDViRaXU1Eb8mpSoj9');

            this.props.history.push(role.mainPage);*/

        }
    }

    render() {
        const {language} = this.props;

        if (language !== null) {
            return (
                <Row className="h-100 justify-content-center align-items-center">
                    <Col xs={12} sm={8} md={4} className="mx-auto">
                        <Card className="login-card">
                            <CardBody className="text-center">
                                <Row>
                                    <Col xs={8}>
                                        <div className='logo'/>
                                    </Col>
                                    <Col xs={4} className={'text-right'}>
                                        <LanguageButton buttonClass={'btn btn-sm btn-outline-dark'}/>
                                    </Col>
                                </Row>
                                <hr/>

                                <h3>{language.welcomeTo}</h3>
                                <h1 className={'mb-3'}>{language.appName}</h1>

                                <FormGroup>
                                    <input type="text" name={'username'}
                                           className="form-control form-control-lg"
                                           onChange={this.handleChange.bind(this)}
                                           placeholder={language.usernameInput}/>
                                </FormGroup>
                                <FormGroup>
                                    <input type="password" name={'password'}
                                           className="form-control form-control-lg"
                                           onChange={this.handleChange.bind(this)}
                                           placeholder={language.passwordInput}/>
                                </FormGroup>
                                <FormGroup>
                                    <Button color={'primary'} block={true} size={'lg'}
                                            onClick={this.handleSubmit.bind(this)}>
                                        <i className="icon icon-arrow-right mr-2"/>
                                        {language.loginButtonText}
                                    </Button>
                                </FormGroup>

                                {this.state.loginError &&
                                <Alert color={'danger'}>
                                    <i className="icon icon-triangle-warning mr-2"/>
                                    {language.loginError}
                                </Alert>
                                }
                            </CardBody>
                            <CardFooter className="text-right">
                                <Copyright/>
                            </CardFooter>
                        </Card>
                    </Col>
                    <Spinner/>
                </Row>
            )
        } else {
            return <></>;
        }
    }
}

function mapStateToProps(state) {
    return {
        language: state.language,
        lang: state.lang,
        serverPath: state.serverPath,
    }
}

function mapStateToDispatch(dispatch) {
    return {
        setUser: (user) => dispatch(setAuthUser(user)),
        setToken: (token) => dispatch(setToken(token)),
        setSpinner: () => dispatch(spinnerToggle()),
    }
}

export default connect(mapStateToProps, mapStateToDispatch)(withRouter(Login));
