import React from 'react';
import {connect} from "react-redux";
import {Modal, ModalHeader, ModalBody} from 'reactstrap';
import {modalImportToggle, spinnerToggle} from "../redux/actions";
import FileUploadProgress from 'react-fileupload-progress';
import XLSX from "xlsx";
import {make_cols} from "./MakeColumns";
import {SheetJSFT} from './types';
import {groupingMap} from '../matrixDataMap';
import RestApiModule from '../RestApiModule';
import {Row, Col, Card, CardBody, CardTitle, CardText, CardHeader, CardFooter, Alert, Button} from 'reactstrap';

import {saveAs} from 'file-saver';

class ModalImportTemplate extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            file: null,
            data: [],
            cols: [],
            fileJSON: {},
            isExcelHeadersValid: null,
            uploadProgress: 0,
            showButton1: true,
            showButton2: false,
            showButton3: false,
        }
        const {REACT_APP_API_GATEWAY, REACT_APP_MATRIX_SERVICE} = process.env;

        this.uploadFileUrl = `${REACT_APP_API_GATEWAY}${REACT_APP_MATRIX_SERVICE}/ImportDataMatrixFromExcel`;
        this.restApi = new RestApiModule();

        this.fileInput = React.createRef();

        this.styleBar = {
            width: 0,
        }
    }


    handleSelectFile(e) {
        const files = e.target.files;

        if (files && files[0])
            this.setState({
                file: files[0],
                showButton2: true
            });
    };

    handleFile() {
        /* Boilerplate to set up FileReader */
        this.props.setSpinner();
        try {

            const reader = new FileReader();
            const rABS = !!reader.readAsBinaryString;
            reader.onload = (e) => {
                /* Parse data */
                const bstr = e.target.result;
                const wb = XLSX.read(bstr, {type: rABS ? 'binary' : 'array', bookVBA: true, sheetRows: 0});
                /* Get first worksheet */
                const wsname = wb.SheetNames[0];
                const ws = wb.Sheets[wsname];

                /* Convert array of arrays */
                const data = XLSX.utils.sheet_to_json(ws, {
                    header: 1,
                    defval: ""
                });

                /* Update state */
                this.setState({data: data, cols: make_cols(ws['!ref'])}, () => {
                    this.state.fileJSON = JSON.stringify(this.state.data, null, 2);
                    this.handleJSONHeaders();

                });
                this.props.setSpinner();
            };

            if (rABS) {
                reader.readAsBinaryString(this.state.file);
            } else {
                reader.readAsArrayBuffer(this.state.file);
            }


        } catch (e) {
            this.setState({showButton3: false})
            this.props.setSpinner();
        }

    }

    handleJSONHeaders () {

        const headerMap = JSON.parse(JSON.stringify(groupingMap));
        const excelHeadersArray = (JSON.parse(this.state.fileJSON))[1];

        const columnsMap = headerMap.reduce((fullArr, item) => {
            const cols = item.columns.reduce((arr, col) => {
                if (col.id !== undefined) arr.push(col.id)
                return arr;
            }, []);
            return [...fullArr, ...cols]
        }, [])

        const isSame = JSON.stringify(excelHeadersArray) === JSON.stringify(columnsMap);
        this.setState({isExcelHeadersValid: isSame, showButton3: isSame, showButton2: !isSame});
    }


    handleTxtReport() {
        var blob = new Blob(["Hello, world!"], {type: "text/plain;charset=utf-8"});
        saveAs(blob, "Import Report.txt");
    }


    toggle() {
        this.setState({
            file: null,
            isExcelHeadersValid: null,
            showButton2: false,
            showButton3: false,
        })
        this.props.setModalImport();
    }

    customFormRenderer(onSubmit) {
        const {language} = this.props;

        return (
            <Row>
                <Col>
                    <Button color={'success'} size={"lg"} block
                            hidden={!this.state.showButton3}
                            onClick={(e) => {
                                this.setState({showButton3: false});
                                onSubmit(e)
                            }}>
                        {language.upload}
                        <i className={`icon icon-upload ml-2`}/>
                    </Button>
                </Col>
            </Row>
        );
    }

    formGetter() {
        return JSON.stringify({
            token: this.props.token,
            importedExcelAsJSON: this.state.fileJSON
        });
    }

    customProgressRenderer(progress, hasError, cancelHandler) {
        if (hasError || progress > -1) {

            const {language} = this.props;

            let progressBar = Object.assign({}, this.styleBar);
            progressBar.width = `${progress}%`;

            return (
                <>
                    <Row>
                        <Col>
                            <div className={"progressWrapper text-center"}>
                                <span>Import progress: {`${progress}%`}</span>
                                <div className={"progressBar"} style={progressBar} />
                            </div>
                        </Col>
                    </Row>

                    {progress === 100 &&
                     <>
                         <Row>
                             <Col>
                                 <Card body={false}>
                                     <CardHeader>
                                         {language.importSection.summaryLabel}
                                     </CardHeader>
                                     <CardBody>
                                         <Row>
                                             <Col>
                                                 <Card color={"primary"} inverse>
                                                     <CardBody>
                                                         <CardTitle tag={"h5"}>{language.importSection.totalRowCount}</CardTitle>
                                                         <CardText>{language.importSection.totalRowText}</CardText>
                                                         <Alert tag={"h4"} color={"info"}>365 {language.importSection.rows}</Alert>
                                                     </CardBody>
                                                 </Card>
                                             </Col>
                                             <Col>
                                                 <Card color={"success"} inverse>
                                                     <CardBody>
                                                         <CardTitle tag={"h5"}>{language.importSection.successRowCount}</CardTitle>
                                                         <CardText>{language.importSection.successRowText}</CardText>
                                                         <Alert tag={"h4"} color={"info"}>325 rows</Alert>
                                                     </CardBody>
                                                 </Card>
                                             </Col>
                                             <Col>
                                                 <Card color={"danger"} inverse>
                                                     <CardBody>
                                                         <CardTitle tag={"h5"}>{language.importSection.failedRowCount}</CardTitle>
                                                         <CardText>{language.importSection.failedRowText}</CardText>
                                                         <Alert tag={"h4"} color={"info"}>40 rows</Alert>
                                                     </CardBody>
                                                 </Card>
                                             </Col>
                                         </Row>
                                         <Row>
                                             <Col>
                                                 <hr/>
                                                 <Button color={"primary"} className={"mt-3"} block size={"lg"} outline>
                                                     {language.importSection.downloadLogFile}
                                                     <i className={`icon icon-download-save ml-2`}/>
                                                 </Button>
                                             </Col>
                                         </Row>
                                     </CardBody>
                                 </Card>
                             </Col>
                         </Row>

                     </>
                    }
                </>
            );
        } else {
            return (
                <></>
            )
        }
    }

    /*
    {"successfulRowCount":35,"totalRowProcessed":50,"errorRowCount":15,"exec_status":"success"}
    */
    render() {
        const {language} = this.props;
        if (language) {
            return (
                <Modal isOpen={this.props.open} size={'lg'} centered={true}>
                    <ModalHeader toggle={this.toggle.bind(this)}>{language.importDataMatrixFile}</ModalHeader>
                    <ModalBody className={'import-file'}>

                        <input ref={fileInput => this.fileInput = fileInput} type="file"
                               accept={SheetJSFT} onChange={this.handleSelectFile.bind(this)} hidden={true}  />

                        <Row>
                            <Col md={'12'}>
                                {!this.state.file &&
                                <div className={'choose-file-wrapper'}>
                                    <Button className={"chose-file"} onClick={() => this.fileInput.click()}>
                                        {language.chooseFile}
                                    </Button>
                                </div>
                                }
                                {this.state.file &&
                                <div className={'choose-file-wrapper'}>
                                    <h4>
                                        {language.importSection.selectedFileLabel}
                                        {this.state.file.name}
                                    </h4>
                                </div>
                                }
                            </Col>
                            <hr/>

                            {this.state.file &&
                            <Col>
                                {this.state.showButton2 &&
                                <Button color={'primary'} className={'mt-3 mb-3'} size={"lg"} block
                                        onClick={this.handleFile.bind(this)}>
                                    {language.validate}
                                    <i className={`icon icon-file ml-2`}/>
                                </Button>
                                }
                                {this.state.isExcelHeadersValid === true &&
                                <Alert color="success" className={'mt-2'}>
                                    <i className={`icon icon-check mr-2`}/>
                                    {language.importSection.selectedFileValid}
                                </Alert>
                                }
                                {this.state.isExcelHeadersValid === false &&
                                <Alert color="danger" className={'mt-2'}>
                                    <i className={`icon icon-cross mr-2`}/>
                                    {language.importSection.selectedFileNotValid}
                                </Alert>
                                }
                            </Col>
                            }
                        </Row>

                        <FileUploadProgress
                            url={this.uploadFileUrl}
                            method={"POST"}
                            beforeSend={(request) => {
                                request.setRequestHeader('Content-Type', 'application/json;charset=UTF-8');
                                return request
                            }}
                            onProgress={(e, request, progress) => {
                                console.log('progress', e, request, progress);
                                this.props.setSpinner();
                            }}
                            onLoad={(e, request) => {
                                console.log('load', e, request);
                                this.props.setSpinner();
                            }}
                            onError={(e, request) => {
                                console.log('error', e, request);
                                this.props.setSpinner();
                            }}
                            onAbort={(e, request) => {
                                console.log('abort', e, request);
                                this.props.setSpinner();
                            }}
                            formGetter={this.formGetter.bind(this)}
                            formRenderer={this.customFormRenderer.bind(this)}
                            progressRenderer={this.customProgressRenderer.bind(this)}

                        />
                    </ModalBody>

                </Modal>
            )
        } else {
            return (<></>)
        }
    }
}

function mapStateToProps(state) {
    return {
        open: state.modalImportToggle,
        import: state.import,
        language: state.language,
        token: state.token
    }
}

function mapDispatchToProps(dispatch) {
    return {
        setModalImport: () => dispatch(modalImportToggle()),
        setSpinner: () => dispatch(spinnerToggle()),
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(ModalImportTemplate);
