import React from 'react';
import {connect} from "react-redux";
import {Input} from 'reactstrap';
import RestApiModule from '../RestApiModule'

class SelectRegion extends React.Component {
    _isMounted = false;

    constructor(props) {
        super(props);

        this.state = {
            regions: null
        }

        this.regions = null;
        this.restApi = new RestApiModule();
    }

    componentDidMount() {

        this._isMounted = true;

        this.restApi.callApi('getRegions', {
            token: this.props.token
        }).then((response) => {

            if (this._isMounted) {

                const regions = response.regions.map((item) => {
                    return item.Region;
                });

                this.setState({regions})
            }
        })
    }

    componentWillUnmount() {
        this._isMounted = false;
    }

    render() {
        const {language} = this.props;

        if (language && this.state.regions) {

            return (
                <Input type={'select'} className={'custom-select'}
                       onChange={ev => this.props.handleRegionChange(ev.target.value)}>
                    <option value="0">{language.loadByRegion}</option>
                    {
                    this.state.regions.map((value) => {
                        return (<option key={value} value={value}>{value}</option>)
                    })
                    }
                </Input>
            )

        } else {
            return (
                <></>
            )
        }
    }
}

function mapStateToProps(state) {
    return {
        language: state.language,
    }
}

export default connect(mapStateToProps)(SelectRegion);
