import React from 'react';
import {connect} from "react-redux";
import {Input} from 'reactstrap';

class SelectRegion extends React.Component {

    render() {
        const {language, regions} = this.props;

        if (language && regions) {
            return (
                <Input type={'select'} className={'custom-select'}
                       onChange={ev => this.props.handleRegionChange(ev.target.value)}>
                    <option value="0">{language.loadByRegion}</option>
                    {regions.map((value) => {
                        return (<option key={value} value={value}>{value}</option>)
                    })
                    }
                </Input>
            )
        } else {
            return (
                <></>
            )
        }
    }
}

function mapStateToProps(state) {
    return {
        language: state.language,
        token: state.token,
        regions: state.regions
    }
}

export default connect(mapStateToProps)(SelectRegion);
